import React, {useState} from 'react';


import './App.css';
import {ArrayToDoListPropsType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';


export type FilterPropsType = 'all' | 'active' | 'completed'

function App() {
    console.log('Render component App')
    const title1 = 'What to learn?'


    const [tasks, setTasks] = useState<Array<ArrayToDoListPropsType>> ([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])

    const removeTask = (id: string) => {
        let removeTaskFilter = tasks.filter(t => t.id !== id)
        setTasks(removeTaskFilter)
    }
    const [filter, setFilter] = useState<FilterPropsType>('all')

    let filterForToDoList = tasks
    if (filter === 'active'){
        filterForToDoList = tasks.filter(f => f.isDone === true)
    }
    if (filter === 'completed'){
        filterForToDoList = tasks.filter(f => f.isDone === false)
    }



    return (
        <div className="App">
            <ToDoList title={title1} tasks={tasks} removeTask={removeTask}/>
            {/*<ToDoList title={title2} tasks={tasks}/>*/}
        </div>
    );
}

export default App;
