import React, {useState} from 'react';


import './App.css';
import {ArrayTasksType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';


export type FilterTasksValueType = 'all' | 'active' | 'completed'

function App() {
    console.log('Render component App')
    const title1 = 'What to learn?'


    const [tasks, setTasks] = useState<Array<ArrayTasksType>>([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])
    const [filter, setFilter] = useState<FilterTasksValueType>('all')
    const removeTask = (id: string) => {
        let removeFilterTask = tasks.filter(t => t.id !== id)
        setTasks(removeFilterTask)
    }

    const addTask = () => {
        let newTask = {id: v1(), title: 'Hello', isDone: false}
        let newTasks = [newTask, ...tasks]
        setTasks(newTasks)
    }

    let filterForToDoLIst = tasks
    if (filter === 'active') {
        filterForToDoLIst = tasks.filter(t => !t.isDone)
    }
    if (filter === 'completed') {
        filterForToDoLIst = tasks.filter(t => t.isDone)
    }

    const filterTask = (value: FilterTasksValueType) => {
        setFilter(value)
    }


    return (
        <div className="App">
            <ToDoList title={title1} tasks={filterForToDoLIst} removeTask={removeTask} filterTask={filterTask}/>
            <button></button>
        </div>
    );
}

export default App;
