import React, {useState} from 'react';


import './App.css';
import {PropsArrayType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';

function App() {
    const title1 = 'What to learn?'
    const title2 = 'What to buy?'
    let newTask;


    const [tasks, setTasks] = useState([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])

    const removeTask = (id: string) => {
        let resultTasks = tasks.filter(t => {
            if (!t.id) {
                return true
            }else{
                return false
            }
        })
        console.log(resultTasks)
        setTasks(resultTasks)
    }

    return (
        <div className="App">
            <ToDoList title={title1} tasks={tasks} removeTask={removeTask}/>
            {/*<ToDoList title={title2} tasks={tasks}/>*/}
        </div>
    );
}

export default App;
