import React, {MouseEvent, FC, ChangeEvent} from 'react';
import {FilterTasksValueType} from '../../App';


type PropsType = {
    title: string
    tasks: Array<ArrayTasksType>
    removeTask: (id: string) => void
    filterTask: (value: FilterTasksValueType) => void
    addTask: (title: string) => void
}

export type ArrayTasksType = {
    id: string
    title: string
    isDone: boolean
}


export const ToDoList: FC<PropsType> = (props) => {

    const tasks = props.tasks.map(t => {
        return (
            <li key={t.id}>
                <input type="checkbox" checked={t.isDone}/>{t.title}
                <button onClick={() => props.removeTask(t.id)}>x</button>
            </li>
        )
    })
    const title = 'я уже близко'

    const changeTestTaskHandler = (e: ChangeEvent<HTMLInputElement>) => {
        let text = e.currentTarget.value
    }
    const addTaskHandler = () => {
        props.addTask(title)
    }

    return (
        <div>
            <h3>{props.title}</h3>
            <div>
                <input value={text} onChange={changeTestTaskHandler}/>
                <button onClick={addTaskHandler}>+</button>
            </div>
            <ul>
                {tasks}
            </ul>
            <div>
                <button onClick={() => props.filterTask('all')}>All</button>
                <button onClick={() => props.filterTask('active')}>Active</button>
                <button onClick={() => props.filterTask('completed')}>Completed</button>
            </div>
        </div>
    );
};
