import React, {MouseEvent, FC} from 'react';


type PropsType = {
    title: string
    tasks: Array<PropsArrayType>
    removeTask: (id: string)=>void
}

export type PropsArrayType = {
    id: string
    title: string
    isDone: boolean
}


export const ToDoList: FC<PropsType> = (props) => {


    // const onClickRemoveTaskHandler = (t: MouseEvent<HTMLButtonElement>) => {
    //     // console.log()
    //     props.removeTask(id)
    // }

    const tasks = props.tasks.map(t => {
        return (
            <li key={t.id}>
                <h5>{t.id}</h5>
                <input type="checkbox" checked={t.isDone}/><span>{t.title}</span>
            <button onClick={()=>props.removeTask()}>x</button>
            </li>

        )
    })


    return (
        <div>
            <h3>{props.title}</h3>
            <div>
                <input/>
                <button>+</button>
            </div>
            <ul>
                {tasks}
            </ul>
            <div>
                <button>All</button>
                <button>Active</button>
                <button>Completed</button>
            </div>
        </div>
    );
};
