import React, {MouseEvent, FC, ChangeEvent, KeyboardEvent, useState} from 'react';
import {FilterTasksValueType} from '../../App';
import './ToDoLIst.css'


type PropsType = {
    titleToDoList: string
    tasks: Array<ArrayTasksType>
    removeTask: (id: string) => void
    filterTask: (value: FilterTasksValueType) => void
    addTask: (titleNewTask: string) => void
    changeStatus: (taskID: string, isDone: boolean) => void
}

export type ArrayTasksType = {
    id: string
    title: string
    isDone: boolean
}


export const ToDoList: FC<PropsType> = (props) => {
    const [error, setError] = useState<string | null> (null)


    const tasks = props.tasks.map(t => {
        const removeTaskHandler = () => {
            props.removeTask(t.id)
        }
        const onChangeCheckBoxHandler = (e: ChangeEvent<HTMLInputElement>) => {
            props.changeStatus(t.id, e.currentTarget.checked)
        }
        return (
            <li key={t.id}>
                <input type="checkbox" onChange={onChangeCheckBoxHandler} checked={t.isDone}/>{t.title}
                <button onClick={removeTaskHandler}>x</button>
            </li>
        )
    })
    const onClickFilterAllHandler = () => props.filterTask('all')
    const onClickFilterActiveHandler = () => props.filterTask('active')
    const onClickFilterCompletedHandler = () => props.filterTask('completed')

    const [titleNewTask, setTitleNewTask] = useState ('')
    const onChangeTextHandler = (e: ChangeEvent<HTMLInputElement>) => {
        setTitleNewTask(e.currentTarget.value)
        setError(null)
    }
    const onClickAddTaskTitle = (e: MouseEvent<HTMLButtonElement>) => {
        if (titleNewTask.trim() !== ''){
            props.addTask(titleNewTask.trim())
            setTitleNewTask('')
        }else {
            setError('Field is required')
        }
    }
    const onKeyPressAddTaskHandler = (e: KeyboardEvent<HTMLInputElement>) => {
        if (e.key === 'Enter' && e.ctrlKey){

            if (titleNewTask !== ''){
                props.addTask(titleNewTask.trim())
                setTitleNewTask('')
            }
        }
    }


    return (
        <div>
            <h3>{props.titleToDoList}</h3>
            <div>
                <input
                    value={titleNewTask}
                    onChange={onChangeTextHandler}
                    onKeyPress={onKeyPressAddTaskHandler}
                    className={error ? 'error' : ''}
                />
                <button onClick={onClickAddTaskTitle}>+</button>
                <p className='height'>{error && <p className='error-message'>{error}</p>}</p>
            </div>
            <ul>
                {tasks}
            </ul>
            <div>
                <button onClick={onClickFilterAllHandler}>All</button>
                <button onClick={onClickFilterActiveHandler}>Active</button>
                <button onClick={onClickFilterCompletedHandler}>Completed</button>
            </div>
        </div>
    );
};
