import React, {useState} from 'react';


import './App.css';
import {PropsArrayType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';
import {execFile} from 'child_process';


type PropsHandlerType = 'all' | 'active' | 'completed'

function App() {
    const title1 = 'What to learn?'


    const [tasks, setTasks] = useState<Array<PropsArrayType>>([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])
    const [filter, setFilter] = useState<PropsHandlerType>('all')

    const removeTask = (id: string) => {
        let resultTasks = tasks.filter(t => t.id !== id)
        console.log(resultTasks)
        setTasks(resultTasks)
    }


    let taskForToDoList = tasks
    if (filter === 'completed') {
        taskForToDoList = tasks.filter(t => t.isDone !== true)
        console.log('completed')
    }
    if (filter === 'active') {
        taskForToDoList = tasks.filter(t => t.isDone !== false)
        console.log('active')
    }
    if (filter === 'all')  {
        taskForToDoList = tasks.filter(t => t)
        console.log('all')
    }
    const change = (t: string) => {
        setFilter(taskForToDoList)
    }



    return (
        <div className="App">
            <ToDoList title={title1} tasks={taskForToDoList} removeTask={removeTask} filterTask={filterTask}/>
            {/*<ToDoList title={title2} tasks={tasks}/>*/}
        </div>
    );
}

export default App;
