import React, {MouseEvent, FC} from 'react';
import {PropsHandlerType} from '../../App';



type PropsType = {
    title: string
    tasks: Array<PropsArrayType>
    removeTask: (id: string) => void
    changeFilterTask: (value: PropsHandlerType) => void
}

export type PropsArrayType = {
    id: string
    title: string
    isDone: boolean
}


export const ToDoList: FC<PropsType> = (props) => {
    // const onClickRemoveTaskHandler = (t: MouseEvent<HTMLButtonElement>) => {
    //     // console.log()
    //     props.removeTask(id)
    // }

    const tasks = props.tasks.map(t => {
        return (
            <li key={t.id}>
                <input type="checkbox" checked={t.isDone}/><span>{t.title}</span>
                <button onClick={() => props.removeTask(t.id)}>x</button>
            </li>

        )
    })


    return (
        <div>
            <h3>{props.title}</h3>
            <div>
                <input/>
                <button>+</button>
            </div>
            <ul>
                {tasks}
            </ul>
            <div>
                <button onClick={()=>props.changeFilterTask('all')} >All</button>
                <button onClick={()=>props.changeFilterTask('active')} >Active</button>
                <button onClick={()=>props.changeFilterTask('completed')} >Completed</button>
            </div>
        </div>
    );
};
