import React, {useState} from 'react';


import './App.css';
import {ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';




function App() {
    console.log('Render component App')
    const title1 = 'What to learn?'


    const [tasks, setTasks] = useState([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])
    const [filter, setFilter] = useState ('all')
    const removeTask = (id: string) => {
        let removeFilterTask = tasks.filter(t => t.id !== id)
        setTasks(removeFilterTask)
    }

    let filterForToDoLIst = tasks
    if (filter === 'completed'){
        filterForToDoLIst
    }

    return (
        <div className="App">
            <ToDoList title={title1} tasks={tasks} removeTask={removeTask} filterTask={filterTask}/>
        </div>
    );
}

export default App;
