import React, {useState} from 'react';


import './App.css';
import {PropsArrayType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';
import {execFile} from 'child_process';


type PropsHandlerType = 'all' | 'active' | 'completed'

function App() {
    const title1 = 'What to learn?'


    const [tasks, setTasks] = useState([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])
    //const [filter, setFilter] = useState(tasks)

    const removeTask = (id: string) => {
        let resultTasks = tasks.filter(t => t.id !== id)
        console.log(resultTasks)
        setTasks(resultTasks)
    }


    const filterTask = (t: string) => {
        setTasks([...tasks])
        let filterTasks
        if (t == 'active') {
            filterTasks = tasks.filter(t => t.isDone !== true)
            console.log('active')
            setTasks([...filterTasks])
        } else if (t == 'completed') {
            filterTasks = tasks.filter(t => t.isDone !== false)
            console.log('completed')
            setTasks([...filterTasks])
        } else {
            filterTasks = tasks.filter(t => t)
            console.log('all')
            setTasks([...filterTasks])
        }
        setTasks([...tasks])
    }

    return (
        <div className="App">
            <ToDoList title={title1} tasks={tasks} removeTask={removeTask} filterTask={filterTask}/>
            {/*<ToDoList title={title2} tasks={tasks}/>*/}
        </div>
    );
}

export default App;
