import React, {useState} from 'react';


import './App.css';
import {ArrayTasksType, ToDoList} from './component/ToDoLIst/ToDoList';
import {v1} from 'uuid';


export type FilterTasksValueType = 'all' | 'active' | 'completed'
type ToDoLIstType = {
    id: string
    title: string
    filter: FilterTasksValueType
}


function App() {
    console.log('Render component App')
    const titleToDoList = 'What to learn?'
    const [tasks, setTasks] = useState<Array<ArrayTasksType>>([
        {id: v1(), title: 'HTML&CSS', isDone: true},
        {id: v1(), title: 'JS', isDone: true},
        {id: v1(), title: 'ReactJS', isDone: false},
        {id: v1(), title: 'Redux', isDone: true},
    ])



    const removeTask = (id: string) => {
        let removeFilterTask = tasks.filter(t => t.id !== id)
        setTasks(removeFilterTask)
    }
    const addTask = (titleNewTask: string) => {
        let newAddTask = {id: v1(), title: titleNewTask, isDone: false}
        setTasks([newAddTask, ...tasks])
    }
    const changeTaskStatus = (taskID: string, isDone: boolean) => {
        let task = tasks.find(t => t.id === taskID)
        if (task) {
            task.isDone = isDone
        }
        setTasks([...tasks])
    }

    const [toDoLists, setToDoLists] = useState<Array<ToDoLIstType>> ([
        {id: v1(), title: 'What to learn?', filter: 'all'},
        {id: v1(), title: 'What to buy?', filter: 'completed'},
    ])
    
    const filterTask = (value: FilterTasksValueType, toDoListID: string) => {
        let toDoList = toDoLists.find(tl => tl.id === toDoListID)
        if (toDoList){
            toDoList.filter = value
            setToDoLists([...toDoLists])
        }
    }
    const toDoListRend = toDoLists.map(tl => {



        return <ToDoList
            key={tl.id}
            id={tl.id}
            titleToDoList={tl.title}
            tasks={filterForToDoLIst}
            removeTask={removeTask}
            filterTask={filterTask}
            addTask={addTask}
            changeTaskStatus={changeTaskStatus}
            filter={tl.filter}
        />
    })

    return (
        <div className="App">
            {toDoListRend}
        </div>
    );
}

export default App;
